mapper.select:
SELECT id, req_name as reqName, req_date as reqDate
FROM analysis_request
ORDER BY req_date ASC;

mapper.insert:
INSERT INTO analysis_request (id, req_name, req_date)
select case count(*) when 0 then 1 else max(id) + 1 end,
        #{reqName}, #{reqDate}
from analysis_request;


  SELECT max(id) as id from analysis_request;
